import React, {Component} from "react";
import axios from "axios";

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {clients: [], accounts: []};
    }

    componentDidMount() {
        (async () => {

            const token = this.props.location.state.token;

            const api = axios.create({
                baseURL: 'http://45.77.58.134:8080',
                headers: { 'Authorization': 'Bearer ' + token }
            });

            const clients = await api.get('/clients').data;
            const accounts = await api.get(`/accounts/${clients[0]._id}`);

            this.setState({clients: clients, accounts: accounts.data});
            // console.log(accounts);
        })();
    }

    render() {

        return (
            <div>

                <div className="navbar">
                    {this.state.clients.map(x =>
                        <h3 key={x._id}>
                            {`Welcome ${x.first} ${x.last}`}
                        </h3>
                    )}

                    <div className="navButtons">
                        <a href="GettingStarted.html">Switch Account</a>
                        <br/>
                        <a href="GettingStarted.html">Sign Out</a>
                    </div>
                </div>


                <div className="scrolling-wrapper">
                    <div className="carda"><h2>Home</h2></div>
                    <div className="carda"><h2>Manage Accounts</h2></div>
                    <div className="carda"><h2>Manage beneficiaries</h2></div>
                    <div className="carda"><h2>Make Payments</h2></div>
                    <div className="carda"><h2>Transfer</h2></div>
                    <div className="carda"><h2>Buy</h2></div>
                </div>

                <div className="container">
                    {
                        this.state.accounts.map((account) => (
                            <div key={account._id} className="card-deck">
                                <div className="card">
                                    <div className="card-body text-left">

                                        <p className="card-text">
                                            {`Account: ${account.description}`}
                                        </p>

                                        <p className="card-text">
                                            {`Type: ${account.type}`}
                                        </p>

                                        <p className="card-text">
                                            {`Balance: R${account.balance}`}
                                        </p>
                                    </div>
                                </div>
                            </div>

                        ))
                    }
                </div>

                <ul>
                    {this.state.clients.map(x =>
                        <li key={x._id}>
                            {`${x.first} ${x.last} - ${x.usernames}`}
                        </li>
                    )}
                </ul>
                ;

            </div>
        )
    }
}

export default Home
